<?php // check if there are subs requiredinclude "dbinfo.php";$name = preg_replace("/[^0-9]/", "", $_GET['name']);if ( !isset($name) ) {	return false;}else {	$db = new PDO("mysql:host=$dbhost;dbname=$dbname", $dbuser, $dbpass); // connect to the database	$query = $db->query("SELECT * FROM subs"); 	$rowcount = $query->rowCount();	$resultall = $query->fetchAll(PDO::FETCH_ASSOC);	$subsarray = array();	for ($i = 0; $i < $rowcount; $i++) {		foreach ($resultall[$i] as $key => $var)		{			$k = 0;			if ( $var >= 4 && $key !== 'gameurl' ) {				$content = "<p onclick='addsub(\"$name\",\"".$resultall[$i]['gameurl']."\",\"$key\")' class='sub ".$key."'>A ".str_replace("_", " ", $key)." is needed! Click to join.</p>";//				$content = "<p onclick='addsub(\"$name\",\"".$resultall[$i]['gameurl']."\",\"$key\")' class='sub ".$key."'>A ".str_replace("_", " ", $key)." is needed! Click to join.</p><span id='closesub' data='$key'></span>";				$subsarray[$k]['link'] = $content;				$k = $k + 1;			}		}	}	$subsarray[0]['numsubs'] = $rowcount;}//echo '{"players":'.json_encode($result).'}';header('Content-Type: application/json');echo json_encode($subsarray);?>