<?php // check if user is admin or notinclude "dbinfo.php";$name = preg_replace("/[^0-9]/", "", $_GET['name']);$db = new PDO("mysql:host=$dbhost;dbname=$dbname", $dbuser, $dbpass); // connect to the database$query = $db->query("SELECT * FROM admins"); $resultall = $query->fetchAll(PDO::FETCH_ASSOC);if (empty($name)) { $name = '0'; } // fix for empty user valuefunction isadded($array, $key, $val) {    foreach ($array as $item)        if (isset($item[$key]) && $item[$key] == $val)            return true;    return false;}$added = isadded($resultall, 'steamid64', $name);echo $added;?>