<?php // remove player by force :)include "dbinfo.php";$name = preg_replace("/[^0-9]/", "", $_GET['name']);$db = new PDO("mysql:host=$dbhost;dbname=$dbname", $dbuser, $dbpass); // connect to the database$query = $db->query("SELECT * FROM admins"); $resultall = $query->fetchAll(PDO::FETCH_ASSOC);if (empty($name)) { $name = '0'; } // fix for empty user valuefunction isadded($array, $key, $val) {    foreach ($array as $item)        if (isset($item[$key]) && $item[$key] == $val)            return true;    return false;}$added = isadded($resultall, 'steamid64', $name);echo "<script type='text/javascript'>function admremoveuser(name) {		reload();		$.ajax({			url: './php/admsurpriseremoveuser.php',			data: {'name': name},			success: function(data) { reload(); }		});}</script>";if ($added == '1') {	$query = $db->query("SELECT * FROM currentplayers");	$result = $query->fetchAll(PDO::FETCH_ASSOC);	for ($i = 0; $i < 18; $i++) {		if ( $result[$i]['name'] != null ) {			echo "<div class='button-status-remove' id='".$result[$i]['class']."' onclick='admremoveuser(\"".$result[$i]['name']."\")'><p style='margin-top:5px;'>".$result[$i]['nickname']."</p></div>";		}	}	echo "<a id='close' class='close-reveal-modal'>CLOSE</a>";} else {	echo "Are you sure you can do that?";}?>