<?php // remove user/player from the database/pickupsession_start();$name = preg_replace("/[^0-9]/", "", $_GET['name']);$links = array();$k = 0;if ($name = $_SESSION['playersteamid64']) {	$dirname = "../logs/";	$logs = glob($dirname."*.json");	foreach($logs as $log) {		$jsonlink = file_get_contents($log);		$json = json_decode($jsonlink, true);		for ($i = 0; $i < 18; $i++) {			if ($name == $json[$i]['name']) {				$links[$k]['team'] = $json[$i]['team'];				$links[$k]['class'] = $json[$i]['class'];				$links[$k]['date'] = date ("d.m.Y H:i", filemtime($log));				$log = str_replace("../logs/","", $log);				$log = str_replace(".json","", $log);				$links[$k]['link'] = $log;				$k = $k + 1;			}		}	}	echo "<table class='matches'><tr><td style='border-bottom:1px solid #FBECCB;width:60px;'>Team</td><td style='border-bottom:1px solid #FBECCB;width:100px;'>Class</td><td style='border-bottom:1px solid #FBECCB;width:110px;'>Date</td><td style='border-bottom:1px solid #FBECCB;'>Match page</td></tr>";	for ($j = 0; $j < count($links); $j++) {		echo "<tr><td>".$links[$j]['team']."</td><td>".$links[$j]['class']."</td><td>".$links[$j]['date']."</td><td><a target='_blank' href='./php/startpickup.php?gameurl=".$links[$j]['link']."'>Link</td></tr>";	}	echo "</table>";}?>